// src/components/Slider.js
import React, { useState } from 'react';
import Slider from 'react-slick';
import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';

// Slider data
const slides = [
    {
        image: '/assets/slider1.jpg',
        title: 'Beach Paradise',
        description: 'Relax and enjoy the beautiful beaches and crystal-clear waters.',
    },
    {
        image: '/assets/slider2.jpg',
        title: 'Mountain Adventure',
        description: 'Explore the majestic mountains with our guided tours.',
    },
    {
        image: '/assets/slider3.jpg',
        title: 'City Lights',
        description: 'Discover the vibrant city life with our exclusive city tours.',
    },
];

// Custom Next Arrow Component
const NextArrow = ({ onClick }) => {
    return (
        <div 
            className="absolute right-2 top-1/2 transform -translate-y-1/2 bg-gray-800 bg-opacity-75 text-white p-2 rounded-full cursor-pointer z-10 hover:bg-gray-600"
            onClick={onClick}
        >
            <svg className="w-6 h-6" fill="currentColor" viewBox="0 0 20 20">
                <path fillRule="evenodd" d="M10.293 3.293a1 1 0 011.414 0l6 6a1 1 0 01.083 1.32l-.083.094-6 6a1 1 0 01-1.497-1.32l.083-.094L14.585 11H4a1 1 0 01-.117-1.993L4 9h10.585l-4.292-4.293a1 1 0 010-1.414z" clipRule="evenodd" />
            </svg>
        </div>
    );
};

// Custom Previous Arrow Component
const PrevArrow = ({ onClick }) => {
    return (
        <div 
            className="absolute left-2 top-1/2 transform -translate-y-1/2 bg-gray-800 bg-opacity-75 text-white p-2 rounded-full cursor-pointer z-10 hover:bg-gray-600"
            onClick={onClick}
        >
            <svg className="w-6 h-6" fill="currentColor" viewBox="0 0 20 20">
                <path fillRule="evenodd" d="M9.707 16.707a1 1 0 01-1.414 0l-6-6a1 1 0 01-.083-1.32l.083-.094 6-6a1 1 0 011.497 1.32l-.083.094L4.415 9H15a1 1 0 01.117 1.993L15 11H4.415l4.292 4.293a1 1 0 010 1.414z" clipRule="evenodd" />
            </svg>
        </div>
    );
};

const CarouselSlider = () => {
    const [loaded, setLoaded] = useState(false); // Track when the image is loaded

    const settings = {
        dots: true,
        infinite: true,
        speed: 500,
        slidesToShow: 1,
        slidesToScroll: 1,
        autoplay: true,
        autoplaySpeed: 7000, // Stay on each slide for 7 seconds
        nextArrow: <NextArrow />, // Custom Next Arrow
        prevArrow: <PrevArrow />, // Custom Prev Arrow
        beforeChange: () => setLoaded(false), // Reset loaded state before changing slides
        afterChange: () => setLoaded(true), // Ensure the state is set after the change
    };

    return (
        <div className="relative w-full max-w-5xl mx-auto mt-20 rounded-lg overflow-hidden">
            <Slider {...settings}>
                {slides.map((slide, index) => (
                    <div key={index} className="relative">
                        <img 
                            src={slide.image} 
                            alt={slide.title} 
                            className="w-full h-96 object-cover"
                            onLoad={() => setLoaded(true)} 
                        />
                        {loaded && (
                            <div className="absolute left-0 bottom-0 p-8 w-full">
                                <div className="bg-gradient-to-r from-black via-transparent to-transparent bg-opacity-40 px-4 py-2 rounded-lg max-w-md animate-slide-up">
                                    <h2 className="text-2xl font-bold mb-2">{slide.title}</h2>
                                    <p className="text-lg">{slide.description}</p>
                                </div>
                            </div>
                        )}
                    </div>
                ))}
            </Slider>
        </div>
    );
}

export default CarouselSlider;
