// src/components/Slider.js
import React, { useState } from 'react';
import Slider from 'react-slick';
import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';

// Slider data
const slides = [
    {
        image: '/assets/slider1.jpg',
        title: 'Beach Paradise',
        description: 'Relax and enjoy the beautiful beaches and crystal-clear waters.',
    },
    {
        image: '/assets/slider2.jpg',
        title: 'Mountain Adventure',
        description: 'Explore the majestic mountains with our guided tours.',
    },
    {
        image: '/assets/slider3.jpg',
        title: 'City Lights',
        description: 'Discover the vibrant city life with our exclusive city tours.',
    },
];

const CarouselSlider = () => {
    const [loadedIndex, setLoadedIndex] = useState(-1); // Track which slide has finished loading

    const settings = {
        dots: true,
        infinite: true,
        speed: 500,
        slidesToShow: 1,
        slidesToScroll: 1,
        autoplay: true,
        autoplaySpeed: 7000, // Stay on each slide for 7 seconds
        arrows: true, // Show arrows for manual navigation
        beforeChange: () => setLoadedIndex(-1), // Reset loaded state before changing slides
    };

    const handleImageLoad = (index) => {
        // Show the text immediately after image load
        setLoadedIndex(index);
    };

    return (
        <div className="relative w-full max-w-5xl mx-auto mt-20 rounded-lg overflow-hidden">
            <Slider {...settings}>
                {slides.map((slide, index) => (
                    <div key={index} className="relative">
                        <img 
                            src={slide.image} 
                            alt={slide.title} 
                            className="w-full h-96 object-cover"
                            onLoad={() => handleImageLoad(index)} 
                        />
                        {loadedIndex === index && (
                            <div className="absolute left-0 bottom-0 p-8 w-full">
                                <div className="bg-gradient-to-r from-black via-transparent to-transparent bg-opacity-40 px-4 py-2 rounded-lg max-w-md animate-slide-up">
                                    <h2 className="text-2xl font-bold mb-2">{slide.title}</h2>
                                    <p className="text-lg">{slide.description}</p>
                                </div>
                            </div>
                        )}
                    </div>
                ))}
            </Slider>
        </div>
    );
}

export default CarouselSlider;
